import React, { useContext, useEffect, useState } from 'react'
import { KeyboardContext } from '../Contexts/KeyboardContext'
import CloseIcon from '@mui/icons-material/Close';
import Switch from '@mui/material/Switch';
import FormControlLabel from '@mui/material/FormControlLabel';
import { styled } from '@mui/material/styles';

const Settings = () => {

    const {settings, showSettings} = useContext(KeyboardContext);
    const [dark, setDark] = useState(false);

    function handleX() {
        showSettings(!settings);
    }
    const GreenSwitch = styled((props) => (
        <Switch onChange={handleDark} focusVisibleClassName=".Mui-focusVisible" disableRipple {...props} />
      ))(({ theme }) => ({
        width: 42,
        height: 26,
        padding: 0,
        '& .MuiSwitch-switchBase': {
          padding: 0,
          margin: 2,
          transitionDuration: '300ms',
          '&.Mui-checked': {
            transform: 'translateX(16px)',
            color: '#fff',
            '& + .MuiSwitch-track': {
              backgroundColor: '#65C466',
              opacity: 1,
              border: 0,
              ...theme.applyStyles('dark', {
                backgroundColor: '#2ECA45',
              }),
            },
            '&.Mui-disabled + .MuiSwitch-track': {
              opacity: 0.5,
            },
          },
          '&.Mui-focusVisible .MuiSwitch-thumb': {
            color: '#33cf4d',
            border: '6px solid #fff',
          },
          '&.Mui-disabled .MuiSwitch-thumb': {
            color: theme.palette.grey[100],
            ...theme.applyStyles('dark', {
              color: theme.palette.grey[600],
            }),
          },
          '&.Mui-disabled + .MuiSwitch-track': {
            opacity: 0.7,
            ...theme.applyStyles('dark', {
              opacity: 0.3,
            }),
          },
        },
        '& .MuiSwitch-thumb': {
          boxSizing: 'border-box',
          width: 22,
          height: 22,
        },
        '& .MuiSwitch-track': {
          borderRadius: 26 / 2,
          backgroundColor: '#878a8c',
          opacity: 1,
          transition: theme.transitions.create(['background-color'], {
            duration: 500,
          }),
          ...theme.applyStyles('dark', {
            backgroundColor: '#39393D',
          }),
        },
      }));

    const OrangeSwitch = styled((props) => (
        <Switch focusVisibleClassName=".Mui-focusVisible" disableRipple {...props} />
      ))(({ theme }) => ({
        width: 42,
        height: 26,
        padding: 0,
        '& .MuiSwitch-switchBase': {
          padding: 0,
          margin: 2,
          transitionDuration: '300ms',
          '&.Mui-checked': {
            transform: 'translateX(16px)',
            color: '#fff',
            '& + .MuiSwitch-track': {
              backgroundColor: '#f5793a',
              opacity: 1,
              border: 0,
              ...theme.applyStyles('dark', {
                backgroundColor: '#2ECA45',
              }),
            },
            '&.Mui-disabled + .MuiSwitch-track': {
              opacity: 0.5,
            },
          },
          '&.Mui-focusVisible .MuiSwitch-thumb': {
            color: '#33cf4d',
            border: '6px solid #fff',
          },
          '&.Mui-disabled .MuiSwitch-thumb': {
            color: theme.palette.grey[100],
            ...theme.applyStyles('dark', {
              color: theme.palette.grey[600],
            }),
          },
          '&.Mui-disabled + .MuiSwitch-track': {
            opacity: 0.7,
            ...theme.applyStyles('dark', {
              opacity: 0.3,
            }),
          },
        },
        '& .MuiSwitch-thumb': {
          boxSizing: 'border-box',
          width: 22,
          height: 22,
        },
        '& .MuiSwitch-track': {
          borderRadius: 26 / 2,
          backgroundColor: '#878a8c',
          opacity: 1,
          transition: theme.transitions.create(['background-color'], {
            duration: 500,
          }),
          ...theme.applyStyles('dark', {
            backgroundColor: '#39393D',
          }),
        },
      }));

    function handleDark() {
      setDark(!dark);
    }

    useEffect(() => {
      if (dark) document.body.style.backgroundColor = 'black';
      else document.body.style.backgroundColor = 'white';
      
    }, [dark])

  return (
    <div className='absolute top-0 left-0 w-screen h-screen z-10 bg-white '>
        <div className='w-[500px] mx-auto'>
            <div className='flex justify-between font-bold tracking-[0.5px] uppercase my-2'>
                <CloseIcon sx={{opacity: '0'}}/>
                <span>Settings</span>
                <CloseIcon className='cursor-pointer' onClick={handleX} sx={{color: '#787c7e'}}/>
            </div>
            
            
            <div className='flex py-2'>
                <div className='basis-4/5'>
                    <div>
                        <div className='text-lg'>Hard Mode</div>
                        <div className='text-gray-500 text-xs'>Any revealed hints must be used in subsequent guesses</div>
                    </div>
                </div>
                <div className='flex basis-1/5 items-center justify-end'>
                    <FormControlLabel className='!mx-0'control={<GreenSwitch sx={{ m: 1, transform: 'scale(0.8)'}}/>}/>
                </div>
            </div>

            <hr/>

            <div className='flex justify-between items-center py-2'>
                
                <div className='text-lg'>Dark Theme</div>
                    
                <FormControlLabel className='!mx-0' control={<GreenSwitch sx={{ m: 1, transform: 'scale(0.8)'}}/>}/>
                
            </div>

            <hr/>

            <div className='flex py-2'>
                <div className='basis-4/5'>
                    <div>
                        <div className='text-lg'>Color Blind Mode</div>
                        <div className='text-gray-500 text-xs'>High contrast colors</div>
                    </div>
                </div>
                <div className='flex basis-1/5 items-center justify-end'>
                    <FormControlLabel className='!mx-0'control={<OrangeSwitch sx={{ m: 1, transform: 'scale(0.8)'}}/>}/>
                </div>
            </div>

            <hr/>
            
            <div className='text-right pr-2 py-4'>
                Twitter
            </div>

            <hr/>

        </div>
    </div>
  )
}

export default Settings
