import React, { useEffect, useState } from 'react'

const Grid = () => {

    const [first, setFirst] = useState('');
    const [firstDone, setFirstDone] = useState(false);
    const [second, setSecond] = useState('');
    const [secondDone, setSecondDone] = useState(false);
    const [third, setThird] = useState('');
    const [thirdDone, setThirdDone] = useState(false);

    const correctWord = "RAT";
    const copyCorrectWord = correctWord

    
    const evalGuess = (guessedWord, pos) => {
        
        if (guessedWord[pos] == correctWord[pos]) {
            copyCorrectWord.replace(copyCorrectWord[pos], '');
            return 'green';
        } else if (copyCorrectWord.includes(guessedWord[pos])) {

            return 'yellow';
        } else {

            return 'gray';
        }

    }
            

    useEffect(() => {
        const onPress = (event) => {
          if (event.key === 'Backspace') {
            if (!firstDone) {
              setFirst((prevStr) => prevStr.slice(0, prevStr.length - 1));
            } else if (firstDone && !secondDone) {
              setSecond((prevStr) => prevStr.slice(0, prevStr.length - 1));
            } else if (firstDone && secondDone && !thirdDone) {
                setThird((prevStr) => prevStr.slice(0, prevStr.length - 1));
              }
          } else if (event.key.match(/[a-zA-Z]/) && event.key.length === 1) {
            if (!firstDone) {
              setFirst((prevStr) => prevStr + event.key.toUpperCase());
            } else if (firstDone && !secondDone) {
              setSecond((prevStr) => prevStr + event.key.toUpperCase());
            } else if (firstDone && secondDone && !thirdDone) {
                setThird((prevStr) => prevStr + event.key.toUpperCase());
              }
          }
        };
    
        // Attach event listener
        document.addEventListener('keydown', onPress);
    
        // Cleanup function to remove event listener
        return () => {
          document.removeEventListener('keydown', onPress);
        };
      }, [firstDone, secondDone]); // Depend only on the completion status

      
    useEffect(() => {

        if (first.length == 3) {
            setFirstDone(true);
        }

    }, [first])


    useEffect(() => {

        if (second.length == 3) {
            setSecondDone(true);

        }

    }, [second])

    useEffect(() => {

        if (third.length == 3) {
            setThirdDone(true);

        }

    }, [third])





  return (
    <div className='mx-auto w-[500px]'>
        <div className='grid grid-cols-3 w-[340px] mx-auto gap-2'>

            { firstDone ?  
            <>
                <div className= { `border-2  ${evalGuess(first, 0) == 'green' ? 'bg-green-200' :  evalGuess(first, 0) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} + ' flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {first[0] || ''}
                </div>
                <div className= { `border-2  ${evalGuess(first, 1) == 'green' ? 'bg-green-200' :  evalGuess(first, 1) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} + ' flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {first[1] || ''}
                </div>
                <div className= { `border-2  ${evalGuess(first, 2) == 'green' ? 'bg-green-200' :  evalGuess(first, 2) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} + ' flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {first[2] || ''}
                </div>   
            </>
            :   
            <>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {first[0] || ''}
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {first[1] || ''}
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {first[2] || ''}
                </div>
            </>
            }

            
            { firstDone && secondDone ?  
            <>
                <div className= { `border-2  ${evalGuess(second[0], 0) == 'green' ? 'bg-green-200' :  evalGuess(second[0], 0) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} + ' flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {second[0] || ''}
                </div>
                <div className= { `border-2  ${evalGuess(second[1], 1) == 'green' ? 'bg-green-200' :  evalGuess(second[1], 1) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} + ' flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {second[1] || ''}
                </div>
                <div className= { `border-2  ${evalGuess(second[2], 2) == 'green' ? 'bg-green-200' :  evalGuess(second[2], 2) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} + ' flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {second[2] || ''}
                </div>   
            </>
            :   
            <>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {second[0] || ''}
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {second[1] || ''}
                    
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {second[2] || ''}
                </div>
            </>
            }

            

            { firstDone && secondDone && thirdDone ?  
            <>
                <div className= { `border-2  ${evalGuess(third[0], 0) == 'green' ? 'bg-green-200' :  evalGuess(third[0], 0) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} + ' flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {third[0] || ''}
                </div>
                <div className= { `border-2  ${evalGuess(third[1], 1) == 'green' ? 'bg-green-200' :  evalGuess(third[1], 1) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} + ' flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {third[1] || ''}
                </div>
                <div className= { `border-2  ${evalGuess(third[2], 2) == 'green' ? 'bg-green-200' :  evalGuess(third[2], 2) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} + ' flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {third[2] || ''}
                </div>   
            </>
            :   
            <>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {third[0] || ''}
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {third[1] || ''}
                    
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {third[2] || ''}
                </div>
            </>
            }
            
        </div>
        
    </div>
  )
}

export default Grid
