import React, { useEffect, useState } from 'react'

const Grid = () => {

    const [first, setFirst] = useState('')
    const [color, setColor] = useState(True)

    const correctWord = 'RAT';

    
    const evalGuess = (letter, pos) => {
        if (letter == correctWord[pos]) {
            setColor(!color)
            return 'green'
        } else if (correctWord.includes(letter)) {
            setColor(!color)

            return 'yellow'
        } else {
            setColor(!color)

            return 'gray'
        }

    }
            

    useEffect(() => {

        document.addEventListener("keydown", function onPress(event) {
               
            if (event.key === 'Backspace'){
                
                setFirst(prevStr => prevStr.slice(0, prevStr.length - 1));
                console.log(first)

            }
            
            else if (event.key.match(/[a-zA-Z]/) && event.key.length == 1) {
                console.log(event.key)
                console.log("NOO")
                setFirst(prevStr => prevStr + event.key.toUpperCase());
            
            } 


        });
        
    }, [])

    // useEffect(() => {

    //     if (first.length == 3) {
            
    //     }
    // }, [first])





  return (
    <div className='mx-auto w-[500px]'>
        <div className='grid grid-cols-3 w-[340px] mx-auto gap-2'>

            {/* {
                first.length != 3

            } */}
            { first.length == 3  ?  
            <>
                <div className= {'border-2 border-green-500 bg-' + evalGuess(first[0], 0) + '-400 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold' }>
                    {first[0] || ''}
                </div>
                <div className= {'border-2 border-green-500 bg-' + evalGuess(first[1], 1) + '-200 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold' }>
                    {first[1] || ''}
                </div>
                <div className= {'border-2 border-green-500 bg-' + evalGuess(first[2], 2) + '-200 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold' }>
                    {first[2] || ''}
                </div>   
            </>
            :   
            <>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {first[0] || ''}
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {first[1] || ''}
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {first[2] || ''}
                </div>
            </>
            }

            


            

            <div className='border-2 border-gray-300 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                
            </div>
            <div className='border-2 border-gray-300 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                
            </div>
            <div className='border-2 border-gray-300 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                
            </div>
            
        </div>
        
    </div>
  )
}

export default Grid
