import React, { useState, useContext, useEffect } from 'react'
import Keyboard from 'react-simple-keyboard';
import "react-simple-keyboard/build/css/index.css";
import './Keys.css'
import BackspaceOutlinedIcon from '@mui/icons-material/BackspaceOutlined';
import inputMask from "simple-keyboard-input-mask";
import { KeyboardContext } from '../Contexts/KeyboardContext';

const Keys = () => {

    const[green, setGreen] = useState('');
    const[yellow, setYellow] = useState('');
    const[gray, setGray] = useState('');


    const {kbColor} = useContext(KeyboardContext);
    

    // useEffect(() => {
        
        console.log(kbColor);

    //     // console.log(kbColor.length);
    //     // for (let i = 0; i < 10; i++) {
    //     //     console.log("here")
    //     // }

    // }, [kbColor])

    // const colorChange = (color) => {
    //     return (
    //     color == 'green' ?
    //         '#6aaa64'
    //     : color == 'yellow' ? 
    //         '#c9b458'
    //     : 
    //         '#787c7e'
    //     )
    // }

    useEffect(() => {

        if (kbColor) {

            kbColor.map((res) => (
                
                // console.log(Object.keys(res))
                // console.log(res[Object.keys(res)[2]])
                Object.keys(res).map((key) => (
                    // document.querySelector(`[data-skbtn=${key.toLowerCase()}]`).style.backgroundColor = colorChange(res[key])
                    // console.log(document.querySelector("kibord").buttonTheme[0])
                    res[key] == "green" 
                    ?
                    setGreen(prevKey => ( {
                        ...prevKey,
                        key
                    }))
                    :
                    res[key] == 'yellow'
                    ?
                    setYellow(prevKey => ( {
                        ...prevKey,
                        key
                    }))
                    :
                    setGray(prevKey => ( {
                        ...prevKey,
                        ...' ',
                        key
                    }))

                    
                ))
                // console.log(res)
            ))
        }

    }, [kbColor])

    

    


    

    const kbChange = (input) => {
        console.log(input)
    }


    return (
        <div className='mx-auto w-[500px] mb-2'>

        <Keyboard 
        className="kibord"
        onChange={kbChange}
            
        modules = {[inputMask]}
        inputMask ={{
            default: {
            mask: '1235',
            regex: /^[a-zA-Z0-9_-]*$/
            }
        }}
        layout={{
            default : [
            'q w e r t y u i o p',
            'a s d f g h j k l',
            'ENTER z x c v b n m DEL'
            ]
        }}
        buttonTheme={[
            {
                class: "green",
                buttons: {green}
            },
            {
                class: "yellow",
                buttons: {yellow}
            },
            {
                class: "gray",
                buttons: {gray}
            },
            {
                class: "buttons",
                buttons: 'q w e r t y u i o p a s d f g h j k l ENTER z x c v b n m DEL'
            }
        ]}
        theme="hg-theme-default board"
        
        />
        </div>
    )
}

export default Keys
