            {/* {
                first.length != 3

            } */}
            { first.length == 3 ?  
            <>
                <div className='border-2 border-green-500 bg-green-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {first[0] || ''}
                </div>
                <div className='border-2 border-green-500 bg-green-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {first[1] || ''}
                </div>
                <div className='border-2 border-green-500 bg-green-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {first[2] || ''}
