import React, { useEffect, useState, useRef } from 'react'
import raw from '../assets/wordle-La.txt'

const Grid = () => {

    const [wordleList, setWordleList] = useState([]);
    const [firstDone, setFirstDone] = useState(false);
    const [secondDone, setSecondDone] = useState(false);
    const [thirdDone, setThirdDone] = useState(false);
    const [fourthDone, setFourthDone] = useState(false);
    const [fifthDone, setFifthDone] = useState(false);
    const [sixthDone, setSixthDone] = useState(false);
    const [loading, setLoading] = useState(true);
    const [correctWord, setCorrectWord] = useState('');

    const[guesses, setGuesses] = useState({first: '', second: '', third: '', fourth: '', fifth: '', sixth: ''});

    const[guessResults, setGuessResults] = useState({first: [], second: [], third: [], fourth: [], fifth: [], sixth: []});
    const firstRef = useRef(false);
    const secondRef = useRef(false);
    const thirdRef = useRef(false);
    const fourthRef = useRef(false);
    const fifthRef = useRef(false);
    const sixthRef = useRef(false);



    useEffect(() => {

        const fetchWords = async () => { 
            const response = await fetch(raw);
            const text = await response.text();
            const lines = text.split('\n').slice(0, 2315);
            setWordleList(lines)
        };
        fetchWords();

    }, []);
    
    useEffect(() => {
        if (wordleList.length > 0) {
        // setCorrectWord(wordleList[Math.floor(Math.random() * 2315)].toUpperCase());
        setCorrectWord("ELBOW")
        setLoading(false);
        }

    }, [wordleList])
    // const correctWord = "FRAUD".toUpperCase();
    // console.log(correctWord);

    var copyCorrectWord = correctWord
    var copyCorrectWord2 = correctWord
    var copyCorrectWord3 = correctWord
    var copyCorrectWord4 = correctWord
    var copyCorrectWord5 = correctWord
    var copyCorrectWord6 = correctWord

    const greenLetter = (dummyWord, guessedWord) => {
        const greenIndex = compareString(correctWord, guessedWord);
        var offset = 0;
        for (let i = 0; i < greenIndex.length; i++) {
            console.log(dummyWord);

            dummyWord = dummyWord.slice(0, [parseInt(greenIndex[i]) - offset]) + dummyWord.slice(parseInt(greenIndex[i]) + 1 - offset);
            offset++;
            console.log(dummyWord);
        
        }
    }

    const evalGuess = (dummyWord, guessedWord, pos) => {
        console.log('dummyword is ', dummyWord)
        console.log('Guessed word', guessedWord[pos])
        if (guessedWord[pos] == correctWord[pos]) {
            return 'green';
        } else if (dummyWord.includes(guessedWord[pos])) {
            // console.log(copyCorrectWord)
            // copyCorrectWord = copyCorrectWord.replace(guessedWord[pos], '')
            copyCorrectWord = copyCorrectWord.slice(0, [parseInt(copyCorrectWord.indexOf(guessedWord[pos]))]) + copyCorrectWord.slice(copyCorrectWord.indexOf(guessedWord[pos])+1);
            // console.log(copyCorrectWord)
            return 'yellow';
        } else {
            return 'gray';
        }
    }

    const compareString = (str1, str2) => {
        var indexMatch = '';
        for (let i = 0; i < str1.length; i++) {
            if (str1[i] == str2[i]) {
                indexMatch += i;
            }
        } return indexMatch;
    }

    
        
    useEffect(() => {
        const onPress = (event) => {
          if (event.key === 'Backspace') {
            if (!firstDone) {
              setGuesses( prevGuess => ({ ...prevGuess, first: (prevGuess.first.slice(0, prevGuess.first.length - 1))}))
            } else if (firstDone && !secondDone) {
                setGuesses( prevGuess => ({ ...prevGuess, second: (prevGuess.second.slice(0, prevGuess.second.length - 1))}))
            } else if (firstDone && secondDone && !thirdDone) {
                setGuesses( prevGuess => ({ ...prevGuess, 'third': prevGuess['third'] = (prevStr) => prevStr.slice(0, prevStr.length - 1)}))
            } else if (firstDone && secondDone && thirdDone && !fourthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, 'fourth': prevGuess['fourth'] = (prevStr) => prevStr.slice(0, prevStr.length - 1)}))
            } else if (firstDone && secondDone && thirdDone && fourthDone && !fifthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, 'fifth': prevGuess['fifth'] = (prevStr) => prevStr.slice(0, prevStr.length - 1)}))
            } else if (firstDone && secondDone && thirdDone && fourthDone && fifthDone && !sixthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, 'sixth': prevGuess['sixth'] = (prevStr) => prevStr.slice(0, prevStr.length - 1)}))
            }
          } else if (event.key.match(/[a-zA-Z]/) && event.key.length === 1) {
            if (!firstDone) {
                setGuesses( prevGuess => ({ ...prevGuess, first: (prevGuess.first  + event.key.toUpperCase())}))
            } else if (firstDone && !secondDone) {
                setGuesses( prevGuess => ({ ...prevGuess, second: (prevGuess.second  + event.key.toUpperCase())}))
            } else if (firstDone && secondDone && !thirdDone) {
                setGuesses( prevGuess => ({ ...prevGuess, third: (prevGuess.third  + event.key.toUpperCase())}))
            } else if (firstDone && secondDone && thirdDone && !fourthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, fourth: (prevGuess.fourth  + event.key.toUpperCase())}))
            } else if (firstDone && secondDone && thirdDone && fourthDone && !fifthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, fifth: (prevGuess.fifth  + event.key.toUpperCase())}))
            } else if (firstDone && secondDone && thirdDone && fourthDone && fifthDone && !sixthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, sixth: (prevGuess.sixth  + event.key.toUpperCase())}))
                
            }
          }
        };
    
        // Attach event listener
        document.addEventListener('keydown', onPress);
    
        // Cleanup function to remove event listener
        return () => {
          document.removeEventListener('keydown', onPress);
        };
      }, [firstDone, secondDone, thirdDone, fourthDone, fifthDone]); // Depend only on the completion status

    useEffect(() => {
        if (guesses['first'].length == 5 && !firstDone) {
            setFirstDone(true);
        } else if (guesses['second'].length == 5 && !secondDone) {
            setSecondDone(true);
        } else if (guesses['third'].length == 5 && !thirdDone) {
            setThirdDone(true);
        } else if (guesses['fourth'].length == 5 && !fourthDone) {
            setFourthDone(true);
        } else if (guesses['fifth'].length == 5 && !fifthDone) {
            setFifthDone(true);
        } else if (guesses['sixth'].length == 5 && !sixthDone) {
            setSixthDone(true);
        }
    }, [guesses])

        useEffect(() => {
            setGuessResults( prevResults => {
                const newResults = {...prevResults};
                if 
                (firstDone && !firstRef.current) {
                    newResults.first =  
                    [   evalGuess(copyCorrectWord, guesses.first, 0), 
                        evalGuess(copyCorrectWord, guesses.first, 1), 
                        evalGuess(copyCorrectWord, guesses.first, 2), 
                        evalGuess(copyCorrectWord, guesses.first, 3), 
                        evalGuess(copyCorrectWord, guesses.first, 4)
                    ];
                    firstRef.current = true;
                } 
                else if 
                (secondDone && !secondRef.current) {
                    newResults.second = 
                    [   evalGuess(copyCorrectWord2, guesses.second, 0), 
                        evalGuess(copyCorrectWord2, guesses.second, 1), 
                        evalGuess(copyCorrectWord2, guesses.second, 2), 
                        evalGuess(copyCorrectWord2, guesses.second, 3), 
                        evalGuess(copyCorrectWord2, guesses.second, 4)
                    ];
                    secondRef.current = true;
                }
                else if 
                (thirdDone && !thirdRef.current) {
                    newResults.second = 
                    [   evalGuess(copyCorrectWord3, guesses.third, 0), 
                        evalGuess(copyCorrectWord3, guesses.third, 1), 
                        evalGuess(copyCorrectWord3, guesses.third, 2), 
                        evalGuess(copyCorrectWord3, guesses.third, 3), 
                        evalGuess(copyCorrectWord3, guesses.third, 4)
                    ];
                    thirdRef.current = true;
                }
                else if 
                (fourthDone && !fourthRef.current) {
                    newResults.second = 
                    [   evalGuess(copyCorrectWord4, guesses.fourth, 0), 
                        evalGuess(copyCorrectWord4, guesses.fourth, 1), 
                        evalGuess(copyCorrectWord4, guesses.fourth, 2), 
                        evalGuess(copyCorrectWord4, guesses.fourth, 3), 
                        evalGuess(copyCorrectWord4, guesses.fourth, 4)
                    ];
                    fourthRef.current = true;
                }
                else if 
                (fifthDone && !fifthRef.current) {
                    newResults.second = 
                    [   evalGuess(copyCorrectWord5, guesses.fifth, 0), 
                        evalGuess(copyCorrectWord5, guesses.fifth, 1), 
                        evalGuess(copyCorrectWord5, guesses.fifth, 2), 
                        evalGuess(copyCorrectWord5, guesses.fifth, 3), 
                        evalGuess(copyCorrectWord5, guesses.fifth, 4)
                    ];
                    fifthRef.current = true;
                }
                else if 
                (sixthDone && !sixthRef.current) {
                    newResults.second = 
                    [   evalGuess(copyCorrectWord6, guesses.sixth, 0), 
                        evalGuess(copyCorrectWord6, guesses.sixth, 1), 
                        evalGuess(copyCorrectWord6, guesses.sixth, 2), 
                        evalGuess(copyCorrectWord6, guesses.sixth, 3), 
                        evalGuess(copyCorrectWord6, guesses.sixth, 4)
                    ];
                    sixthRef.current = true;
                }
            });

        }, [firstDone, secondDone, thirdDone, fourthDone, fifthDone, sixthDone])

    const guessResult = { 
        first: 
        [   evalGuess(copyCorrectWord, guesses.first, 0), 
            evalGuess(copyCorrectWord, guesses.first, 1), 
            evalGuess(copyCorrectWord, guesses.first, 2), 
            evalGuess(copyCorrectWord, guesses.first, 3), 
            evalGuess(copyCorrectWord, guesses.first, 4)], 
        second: 
        [   evalGuess(copyCorrectWord2, guesses.second, 0), 
            evalGuess(copyCorrectWord2, guesses.second, 1), 
            evalGuess(copyCorrectWord2, guesses.second, 2), 
            evalGuess(copyCorrectWord2, guesses.second, 3), 
            evalGuess(copyCorrectWord2, guesses.second, 4)],
        third: 
        [   evalGuess(copyCorrectWord3, guesses.third, 0), 
            evalGuess(copyCorrectWord3, guesses.third, 1), 
            evalGuess(copyCorrectWord3, guesses.third, 2), 
            evalGuess(copyCorrectWord3, guesses.third, 3), 
            evalGuess(copyCorrectWord3, guesses.third, 4)],
        fourth: 
        [   evalGuess(copyCorrectWord4, guesses.fourth, 0), 
            evalGuess(copyCorrectWord4, guesses.fourth, 1), 
            evalGuess(copyCorrectWord4, guesses.fourth, 2), 
            evalGuess(copyCorrectWord4, guesses.fourth, 3), 
            evalGuess(copyCorrectWord4, guesses.fourth, 4)],
        fifth: 
        [   evalGuess(copyCorrectWord5, guesses.fifth, 0), 
            evalGuess(copyCorrectWord5, guesses.fifth, 1), 
            evalGuess(copyCorrectWord5, guesses.fifth, 2), 
            evalGuess(copyCorrectWord5, guesses.fifth, 3), 
            evalGuess(copyCorrectWord5, guesses.fifth, 4)],
        sixth: 
        [   evalGuess(copyCorrectWord6, guesses.sixth, 0), 
            evalGuess(copyCorrectWord6, guesses.sixth, 1), 
            evalGuess(copyCorrectWord6, guesses.sixth, 2), 
            evalGuess(copyCorrectWord6, guesses.sixth, 3), 
            evalGuess(copyCorrectWord6, guesses.sixth, 4)]
    }
    

    


    if (loading) {
        return <div>Loading...</div>; // Show a loading indicator while data is being fetched
    } 

  return (
    <div className='mx-auto w-[500px]'>
        <div className='grid grid-cols-5 w-[340px] mx-auto gap-2'>

            { firstDone ? 
            <>
                {greenLetter(copyCorrectWord, guesses.first)}
                
                <div className= { `border-2  ${guessResults.first[0] == 'green' ? 'bg-green-200' :  guessResults.first[0] == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {guesses['first'][0] || ''}
                </div>
