import React, { useEffect, useState } from 'react'
import raw from '../assets/wordle-La.txt'

const Grid = () => {

    const [wordleList, setWordleList] = useState([]);
    const [firstDone, setFirstDone] = useState(false);
    const [secondDone, setSecondDone] = useState(false);
    const [thirdDone, setThirdDone] = useState(false);
    const [fourthDone, setFourthDone] = useState(false);
    const [fifthDone, setFifthDone] = useState(false);
    const [sixthDone, setSixthDone] = useState(false);
    const [loading, setLoading] = useState(true);
    const [correctWord, setCorrectWord] = useState('');

    const[guesses, setGuesses] = useState({first: '', second: '', third: '', fourth: '', fifth: '', sixth: ''})

    useEffect(() => {

        const fetchWords = async () => { 
            const response = await fetch(raw);
            const text = await response.text();
            const lines = text.split('\n').slice(0, 2315);
            setWordleList(lines)
        };
        fetchWords();

    }, []);
    
    useEffect(() => {
        if (wordleList.length > 0) {
        // setCorrectWord(wordleList[Math.floor(Math.random() * 2315)].toUpperCase());
        setCorrectWord("ELBOW")
        setLoading(false);
        }

    }, [wordleList])
    // const correctWord = "FRAUD".toUpperCase();
    console.log(correctWord);

    var copyCorrectWord = correctWord
    var copyCorrectWord2 = correctWord
    var copyCorrectWord3 = correctWord
    var copyCorrectWord4 = correctWord
    var copyCorrectWord5 = correctWord
    var copyCorrectWord6 = correctWord

    const greenLetter = (dummyWord, guessedWord) => {
        const greenIndex = compareString(correctWord, guessedWord);
        var offset = 0;
        for (let i = 0; i < greenIndex.length; i++) {
            dummyWord = dummyWord.slice(0, [parseInt(greenIndex[i]) - offset]) + dummyWord.slice(parseInt(greenIndex[i])+1 - offset);
            offset++;

        }
    }

    const evalGuessr = (dummyWord, guessedWord, pos) => {
        if (guessedWord[pos] == correctWord[pos]) {
            return 'green';
        } else if (dummyWord.includes(guessedWord[pos])) {
            // console.log(copyCorrectWord)
            // copyCorrectWord = copyCorrectWord.replace(guessedWord[pos], '')
            // copyCorrectWord = copyCorrectWord.slice(0, [parseInt(copyCorrectWord.indexOf(guessedWord[pos]))]) + copyCorrectWord.slice(copyCorrectWord.indexOf(guessedWord[pos])+1);
            // console.log(copyCorrectWord)
            // console.log('Guessed word', guessedWord[pos])
            return 'yellow';
        } else {
            return 'gray';
        }

    }
    
    
    


    const evalGuess = (guessedWord, pos) => {
        // console.log("Guessed Word is", guessedWord[pos])
        if (guessedWord[pos] == correctWord[pos]) {
            return 'green';
        } else if (copyCorrectWord.includes(guessedWord[pos])) {
            
            
            return 'yellow';
        } else {
            
            console.log("HOW", guessedWord[pos])
            return 'gray';
        }
    }

    const evalGuess2 = (guessedWord, pos) => {
        if (guessedWord[pos] == correctWord[pos]) {
            return 'green';
        } else if (copyCorrectWord2.includes(guessedWord[pos])) {
            return 'yellow';
        } else {
            return 'gray';
        }
    }

    const evalGuess3 = (guessedWord, pos) => {
        if (guessedWord[pos] == correctWord[pos]) {
            return 'green';
        } else if (copyCorrectWord3.includes(guessedWord[pos])) {
            return 'yellow';
        } else {
            return 'gray';
        }
    }

    const evalGuess4 = (guessedWord, pos) => {
        if (guessedWord[pos] == correctWord[pos]) {
            return 'green';
        } else if (copyCorrectWord4.includes(guessedWord[pos])) {
            return 'yellow';
        } else {
            return 'gray';
        }
    }

    const evalGuess5 = (guessedWord, pos) => {
        if (guessedWord[pos] == correctWord[pos]) {
            return 'green';
        } else if (copyCorrectWord5.includes(guessedWord[pos])) {
            return 'yellow';
        } else {
            return 'gray';
        }
    }

    
    const evalGuess6 = (guessedWord, pos) => {
        if (guessedWord[pos] == correctWord[pos]) {
            return 'green';
        } else if (copyCorrectWord6.includes(guessedWord[pos])) {
            return 'yellow';
        } else {
            return 'gray';
        }
    }

    const compareString = (str1, str2) => {
        var indexMatch = '';
        for (let i = 0; i < str1.length; i++) {
            if (str1[i] == str2[i]) {
                indexMatch += i;
            }
        } return indexMatch;
    }

    
        
    useEffect(() => {
        const onPress = (event) => {
          if (event.key === 'Backspace') {
            if (!firstDone) {
              setGuesses( prevGuess => ({ ...prevGuess, first: (prevGuess.first.slice(0, prevGuess.first.length - 1))}))
            } else if (firstDone && !secondDone) {
                setGuesses( prevGuess => ({ ...prevGuess, second: (prevGuess.second.slice(0, prevGuess.second.length - 1))}))
            } else if (firstDone && secondDone && !thirdDone) {
                setGuesses( prevGuess => ({ ...prevGuess, 'third': prevGuess['third'] = (prevStr) => prevStr.slice(0, prevStr.length - 1)}))
            } else if (firstDone && secondDone && thirdDone && !fourthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, 'fourth': prevGuess['fourth'] = (prevStr) => prevStr.slice(0, prevStr.length - 1)}))
            } else if (firstDone && secondDone && thirdDone && fourthDone && !fifthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, 'fifth': prevGuess['fifth'] = (prevStr) => prevStr.slice(0, prevStr.length - 1)}))
            } else if (firstDone && secondDone && thirdDone && fourthDone && fifthDone && !sixthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, 'sixth': prevGuess['sixth'] = (prevStr) => prevStr.slice(0, prevStr.length - 1)}))
            }
          } else if (event.key.match(/[a-zA-Z]/) && event.key.length === 1) {
            if (!firstDone) {
                setGuesses( prevGuess => ({ ...prevGuess, first: (prevGuess.first  + event.key.toUpperCase())}))
            } else if (firstDone && !secondDone) {
                setGuesses( prevGuess => ({ ...prevGuess, second: (prevGuess.second  + event.key.toUpperCase())}))
            } else if (firstDone && secondDone && !thirdDone) {
                setGuesses( prevGuess => ({ ...prevGuess, third: (prevGuess.third  + event.key.toUpperCase())}))
            } else if (firstDone && secondDone && thirdDone && !fourthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, fourth: (prevGuess.fourth  + event.key.toUpperCase())}))
            } else if (firstDone && secondDone && thirdDone && fourthDone && !fifthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, fifth: (prevGuess.fifth  + event.key.toUpperCase())}))
            } else if (firstDone && secondDone && thirdDone && fourthDone && fifthDone && !sixthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, sixth: (prevGuess.sixth  + event.key.toUpperCase())}))
                
            }
          }
        };
    
        // Attach event listener
        document.addEventListener('keydown', onPress);
    
        // Cleanup function to remove event listener
        return () => {
          document.removeEventListener('keydown', onPress);
        };
      }, [firstDone, secondDone, thirdDone, fourthDone, fifthDone]); // Depend only on the completion status

    useEffect(() => {
        if (guesses['first'].length == 5 && !firstDone) {
            setFirstDone(true);
        } else if (guesses['second'].length == 5 && !secondDone) {
            setSecondDone(true);
        } else if (guesses['third'].length == 5 && !thirdDone) {
            setThirdDone(true);
        } else if (guesses['fourth'].length == 5 && !fourthDone) {
            setFourthDone(true);
        } else if (guesses['fifth'].length == 5 && !fifthDone) {
            setFifthDone(true);
        } else if (guesses['sixth'].length == 5 && !sixthDone) {
            setSixthDone(true);
        }
    }, [guesses])

    if (loading) {
        return <div>Loading...</div>; // Show a loading indicator while data is being fetched
    } 

  return (
    <div className='mx-auto w-[500px]'>
        <div className='grid grid-cols-5 w-[340px] mx-auto gap-2'>

            { firstDone ? 
            <>
                {greenLetter(copyCorrectWord, guesses.first)}
                <div className= { `border-2  ${evalGuess(copyCorrectWord, guesses.first, 0) == 'green' ? 'bg-green-200' :  evalGuess(copyCorrectWord, guesses.first, 0) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {guesses['first'][0] || ''}
                </div>
                <div className= { `border-2  ${evalGuess(copyCorrectWord, guesses.first, 1) == 'green' ? 'bg-green-200' :  evalGuess(copyCorrectWord, guesses.first, 1) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {guesses['first'][1] || ''}
                </div>
                <div className= { `border-2  ${evalGuess(copyCorrectWord, guesses.first, 2) == 'green' ? 'bg-green-200' :  evalGuess(copyCorrectWord, guesses.first, 2) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {guesses['first'][2] || ''}
                </div>   
                <div className= { `border-2  ${evalGuess(copyCorrectWord, guesses.first, 3) == 'green' ? 'bg-green-200' :  evalGuess(copyCorrectWord, guesses.first, 3) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {guesses['first'][3] || ''}
                </div>
                <div className= { `border-2  ${evalGuess(copyCorrectWord, guesses.first, 4) == 'green' ? 'bg-green-200' :  evalGuess(copyCorrectWord, guesses.first, 4) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {guesses['first'][4] || ''}
                </div>   
            </>
            :   
            <>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {guesses['first'][0] || ''}
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {guesses['first'][1] || ''}
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {guesses['first'][2] || ''}
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {guesses['first'][3] || ''}
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {guesses['first'][4] || ''}
                </div>
                
            </>
            }

            
            { firstDone && secondDone ?  
            <>
                {greenLetter(copyCorrectWord, guesses.second)}
                <div className= { `border-2  ${evalGuess2(guesses['second'], 0) == 'green' ? 'bg-green-200' :  evalGuess2(guesses['second'], 0) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} + ' flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {guesses['second'][0] || ''}
                </div>
                <div className= { `border-2  ${evalGuess2(guesses['second'], 1) == 'green' ? 'bg-green-200' :  evalGuess2(guesses['second'], 1) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} + ' flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {guesses['second'][1] || ''}
                </div>
                <div className= { `border-2  ${evalGuess2(guesses['second'], 2) == 'green' ? 'bg-green-200' :  evalGuess2(guesses['second'], 2) == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} + ' flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {guesses['second'][2] || ''}
                </div>  
