        default : [
          'q w e r t y u i o p',
          'a s d f g h j k l',
          'enter z x c v b n m back'
        ]
      }}
      buttonTheme={[
        {
          class: "buttons",
          buttons: 'q w e r t y u i o p a s d f g h j k l enter z x c v b n m back'
        }
      ]}
      theme="hg-theme-default board"
      

      />
    </div>
  )
}

export default Keys
