import React, { useEffect, useState, useRef } from 'react'
import raw from '../assets/wordle-La.txt'

const Grid = () => {

    const [wordleList, setWordleList] = useState([]);
    const [firstDone, setFirstDone] = useState(false);
    const [secondDone, setSecondDone] = useState(false);
    const [thirdDone, setThirdDone] = useState(false);
    const [fourthDone, setFourthDone] = useState(false);
    const [fifthDone, setFifthDone] = useState(false);
    const [sixthDone, setSixthDone] = useState(false);
    const [loading, setLoading] = useState(true);
    const [correctWord, setCorrectWord] = useState('');

    const[guesses, setGuesses] = useState({first: '', second: '', third: '', fourth: '', fifth: '', sixth: ''});
    const [dummyWords, setDummyWords] = useState({first: '', second: '', third: '', fourth: '', fifth: '', sixth: ''});

    useEffect(() => {

        const fetchWords = async () => { 
            const response = await fetch(raw);
            const text = await response.text();
            const lines = text.split('\n').slice(0, 2315);
            setWordleList(lines)
        };
        fetchWords();

    }, []);
    
    useEffect(() => {
        if (wordleList.length > 0) {
        // setCorrectWord(wordleList[Math.floor(Math.random() * 2315)].toUpperCase());
        setCorrectWord("ELBOW")
        setLoading(false);
        }
    }, [wordleList])

    useEffect(() => {
        setDummyWords({first: correctWord, second: correctWord, third: correctWord, fourth: correctWord, fifth: correctWord, sixth: correctWord})
    }, [correctWord])


    useEffect(() => {
        const onPress = (event) => {
          if (event.key === 'Backspace') {
            if (!firstDone) {
              setGuesses( prevGuess => ({ ...prevGuess, first: (prevGuess.first.slice(0, prevGuess.first.length - 1))}))
            } else if (!secondDone) {
                setGuesses( prevGuess => ({ ...prevGuess, second: (prevGuess.second.slice(0, prevGuess.second.length - 1))}))
            } else if (!thirdDone) {
                setGuesses( prevGuess => ({ ...prevGuess, 'third': prevGuess['third'] = (prevStr) => prevStr.slice(0, prevStr.length - 1)}))
            } else if (!fourthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, 'fourth': prevGuess['fourth'] = (prevStr) => prevStr.slice(0, prevStr.length - 1)}))
            } else if (!fifthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, 'fifth': prevGuess['fifth'] = (prevStr) => prevStr.slice(0, prevStr.length - 1)}))
            } else if (!sixthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, 'sixth': prevGuess['sixth'] = (prevStr) => prevStr.slice(0, prevStr.length - 1)}))
            }
          } else if (event.key.match(/[a-zA-Z]/) && event.key.length === 1) {
            if (!firstDone) {
                setGuesses( prevGuess => ({ ...prevGuess, first: (prevGuess.first  + event.key.toUpperCase())}))
            } else if (!secondDone) {
                setGuesses( prevGuess => ({ ...prevGuess, second: (prevGuess.second  + event.key.toUpperCase())}))
            } else if (!thirdDone) {
                setGuesses( prevGuess => ({ ...prevGuess, third: (prevGuess.third  + event.key.toUpperCase())}))
            } else if (!fourthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, fourth: (prevGuess.fourth  + event.key.toUpperCase())}))
            } else if (!fifthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, fifth: (prevGuess.fifth  + event.key.toUpperCase())}))
            } else if (!sixthDone) {
                setGuesses( prevGuess => ({ ...prevGuess, sixth: (prevGuess.sixth  + event.key.toUpperCase())}))
                
            }
          }
        };
    
        // Attach event listener
        document.addEventListener('keydown', onPress);
    
        // Cleanup function to remove event listener
        return () => {
          document.removeEventListener('keydown', onPress);
        };
      }, [firstDone, secondDone, thirdDone, fourthDone, fifthDone]); // Depend only on the completion status

    const greenLetter = (dummyWord, key, guessedWord) => {
        const greenIndex = compareString(correctWord, guessedWord);
        var offset = 0;
        for (let i = 0; i < greenIndex.length; i++) {
            dummyWord = dummyWord.slice(0, [parseInt(greenIndex[i]) - offset]) + dummyWord.slice(parseInt(greenIndex[i]) + 1 - offset);
            setDummyWords(prevState => ({
                ...prevState, 
                [key]: dummyWord
            }));
            offset++;
        }
    }
    const [yellow, setYellow] = useState(useState({first: [], second: [], third: [], fourth: [], fifth: [], sixth: []}));

    const [color, setColor] = useState({first: ['gray', 'gray','gray','gray','gray']});
    
    // useEffect(() => {


    // }, [yellow])
    // const checkYellow = () => {
        
    // }

    const evalGuess = async (dummyWord, key, guessedWord, pos) => {
        return new Promise ((resolve) => {
            if (guessedWord[pos] == correctWord[pos]) {
                resolve('green');
            } else if (dummyWord.includes(guessedWord[pos] && !(guessedWord[pos] == correctWord[pos]) )) {
                // dummyWord = dummyWord.replace(guessedWord[pos], '')
                dummyWord = dummyWord.slice(0, [parseInt(dummyWord.indexOf(guessedWord[pos]))]) + dummyWord.slice(dummyWord.indexOf(guessedWord[pos])+1);
                setDummyWords(prevState => ({
                    ...prevState, 
                    [key]: dummyWord
                }));
                resolve('yellow');
                // setYellow(prevYellow => ({
                //     ...prevYellow,
                //     first: [...prevResults.first, guessWord[pos]]
                // }));
            } else {
                resolve('gray');
            }
        }, 100);
       
        
        
    };

    const compareString = (str1, str2) => {
        var indexMatch = '';
        for (let i = 0; i < str1.length; i++) {
            if (str1[i] == str2[i]) {
                indexMatch += i;
            }
        } return indexMatch;
    }

    useEffect(() => {
        if (guesses.first.length == 5 && !firstDone) {
            setFirstDone(true);
        } else if (guesses['second'].length == 5 && !secondDone) {
            setSecondDone(true);
        } else if (guesses['third'].length == 5 && !thirdDone) {
            setThirdDone(true);
        } else if (guesses['fourth'].length == 5 && !fourthDone) {
            setFourthDone(true);
        } else if (guesses['fifth'].length == 5 && !fifthDone) {
            setFifthDone(true);
        } else if (guesses['sixth'].length == 5 && !sixthDone) {
            setSixthDone(true);
        }
    }, [guesses])

    
    const [guessResults, setGuessResults] = useState({first: [], second: [], third: [], fourth: [], fifth: [], sixth: []})
    const [firstGuess, setFirstGuess] =  useState(false);
   


    useEffect(() => {
        if (firstDone) {
            const evaluateGuesses = async () => {
                const guess0 = await evalGuess(dummyWords.first, 'first', guesses.first, 0)
                const guess1 = await evalGuess(dummyWords.first, 'first', guesses.first, 1)
                const guess2 = await evalGuess(dummyWords.first, 'first', guesses.first, 2)
                const guess3 = await evalGuess(dummyWords.first, 'first', guesses.first, 3)
                const guess4 = await evalGuess(dummyWords.first, 'first', guesses.first, 4)
            setGuessResults(prevResults => ({
                ...prevResults,
                first: [...prevResults.first, guess0]
            }));
        }
            evaluateGuesses();
            

    }

        // Repeat this pattern for the remaining guesses (third, fourth, etc.)
    }, [ dummyWords, firstDone, secondDone, thirdDone, fourthDone, fifthDone, sixthDone]);

    


    if (loading) {
        return <div>Loading...</div>; // Show a loading indicator while data is being fetched
    } 

  return (
    <div className='mx-auto w-[500px]'>
        <div className='grid grid-cols-5 w-[340px] mx-auto gap-2'>

            { firstDone ? 
            <>
                {greenLetter(dummyWords.first, 'first', guesses.first)}
                
                <div className= { `border-2  ${guessResults.first[0] == 'green' ? 'bg-green-200' :  guessResults.first[0] == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {guesses['first'][0] || ''}
                </div>
                <div className= { `border-2  ${guessResults.first[1] == 'green' ? 'bg-green-200' :  guessResults.first[1] == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {guesses['first'][1] || ''}
                </div>
                <div className= { `border-2  ${guessResults.first[2] == 'green' ? 'bg-green-200' :  guessResults.first[2] == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {guesses['first'][2] || ''}
                </div>   
                <div className= { `border-2  ${guessResults.first[3] == 'green' ? 'bg-green-200' :  guessResults.first[3] == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {guesses['first'][3] || ''}
                </div>
                <div className= { `border-2  ${guessResults.first[4] == 'green' ? 'bg-green-200' :  guessResults.first[4] == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'} flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
                    {guesses['first'][4] || ''}
                </div>   
            </>
            :   
            <>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {guesses['first'][0] || ''}
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {guesses['first'][1] || ''}
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {guesses['first'][2] || ''}
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {guesses['first'][3] || ''}
                </div>
                <div className='border-2 border-gray-500 flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold'>
                    {guesses['first'][4] || ''}
                </div>
                
            </>
            }

            
            {/* { firstDone && secondDone ?  
            <>
                {greenLetter(copyCorrectWord2, guesses.second)}
                <div className= { `border-2  ${guessResult.second[0] == 'green' ? 'bg-green-200' :  guessResult.second[0] == 'yellow' ? 'bg-yellow-200' : 'bg-gray-400'}  flex items-center justify-center w-[64px] h-[64px] uppercase text-4xl font-bold` }>
